function openModal(title, id, fullName, email, role) {
    console.log("Opening modal with values:", title, id, fullName, email, role); // Debugging log

    document.getElementById("userModalLabel").innerText = title;

    document.getElementById("id").value = id || "";
    document.getElementById("fullName").value = fullName || "";
    document.getElementById("email").value = email || "";
    document.getElementById("role").value = role || "TOUR_MANAGER"; // Giá trị mặc định

    // Nếu là Edit User, ẩn trường password
    if (title === "Edit User") {
        document.getElementById("password").parentElement.style.display = "none";
    } else {
        document.getElementById("password").parentElement.style.display = "block";
    }

    // Hiển thị modal
    $('#userModal').modal('show');
}

(function ($) {
    "use strict";

    $("#sidebarToggle").on("click", function (o) {
        o.preventDefault();
        $("body").toggleClass("sidebar-toggled");
        $(".sidebar").toggleClass("toggled");
    });

    $("body.fixed-nav .sidebar").on("mousewheel DOMMouseScroll wheel", function (o) {
        if ($(window).width() > 768) {
            var e = o.originalEvent,
                t = e.wheelDelta || -e.detail;
            this.scrollTop += 30 * (t < 0 ? 1 : -1);
            o.preventDefault();
        }
    });

    $(document).on("scroll", function () {
        $(this).scrollTop() > 100 ? $(".scroll-to-top").fadeIn() : $(".scroll-to-top").fadeOut();
    });

    $(document).on("click", "a.scroll-to-top", function (o) {
        var e = $(this);
        $("html, body").stop().animate(
            {scrollTop: $(e.attr("href")).offset().top},
            1000,
            "easeInOutExpo"
        );
        o.preventDefault();
    });

})(jQuery);


